const faqItems=document.querySelectorAll(".faq__item");faqItems.forEach((e=>{const l=e.querySelector(".faq__question"),n=e.querySelector(".faq__icon"),t=e.querySelector(".faq__icon.minus-icon"),c=e.querySelector(".faq__input");l.addEventListener("click",(()=>{c.checked?(n.style.display="inline-block",t.style.display="none"):(n.style.display="none",t.style.display="inline-block")}))}));