document.querySelector(".promo__scroll-title").addEventListener("click",(function(e){e.preventDefault();const t=document.querySelector(this.getAttribute("href"));window.scrollTo({top:t.offsetTop,behavior:"smooth"})}));const faqItems=document.querySelectorAll(".faq__item");faqItems.forEach((e=>{const t=e.querySelector(".faq__question"),o=e.querySelector(".faq__icon"),l=e.querySelector(".faq__icon.minus-icon"),c=e.querySelector(".faq__input"),n=e.querySelector(".faq__text");t.addEventListener("click",(()=>{c.checked?(o.style.display="inline-block",l.style.display="none",n.classList.remove("expanded")):(o.style.display="none",l.style.display="inline-block",n.classList.add("expanded"))}))}));